import React from "react";
import logo from "./logo.svg";
import "./App.css";
import Header from "./components/Header";
import Form from "./components/Form";
import Table from "./components/Table";
import '../public/jquery-3.5.1'
import '../public/datatable/datatables.css'
import '../public/datatable/datatables.js'

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = ({
      headerName: "Create",
    });

    //  this.handleDeleteButton = this.handleDeleteButton.bind(this);
    // this.handleUpdateButton = this.handleUpdateButton.bind(this);

    //this.refs.form.existingData(1);
  }

  handleUpdateButton = (value) => {
    this.setState({
      headerName: value,
    });
    console.log("Handle Update : " + value);
    //alert('Handle Update');
  }

  handleDeleteButton = () => {
    // this.setState({
    //   headerName: value,
    // });
    console.log("Handle delete");
    alert('Handle Delete');
  }


  triggerChildAlert(){
    this.refs.form.existingData(1);
}
  render() {
    return (

      <div className="container-fluid">
        <button onClick={this.triggerChildAlert.bind(this)}>Click</button>
        <Header  />
        <div className="row">
          <div className="col-md-6">
            <Form headerName={this.state.headerName} ref="form"/>
          </div>
          <div className="col-md-6">
            <Table
              onUpdateButton={(value) => this.handleUpdateButton(value)}
              onDeleteButton={this.handleDeleteButton}
            />
          </div>
        </div>
      </div>
    );
  }
}
