const $ = require("jquery");
$.DataTable = require("datatables.net");

export default class Table extends Component {


  constructor(props){
    super(props);
    this.state = {
      data : null,
    }
  }

  componentDidMount() {
    this.$el = $(this.el);
    this.$el.DataTable({
      columns: [
        { title: "Name" },
        { title: "Email" },
        { title: "Country" },
        { title: "Married" },
        { title: "" },
        { title: "" },
      ],
    });
  }

