  render() {
    return (
      <div>
        <table className="display" width="100%" ref={(el) => (this.el = el)}>
          <tr>
            <td>myname</td>
            <td>mymeail@gmail.com</td>
            <td>mycountry</td>
            <td>no</td>
            <td>
              <button
                type="button"
                className="btn btn-primary"
                onClick={() => this.clickUpdateButton(1)}
              >
                Update
