import React, { Component } from 'react'
import 'jquery/src/jquery'
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.min';



export default class Form extends Component {
    constructor(props){
        super(props);
        this.state = {
            name : '',
            email : '',
            country : '',
            name : '',
        };
    }

    handleChange(event) {
        console.log(event.target.name);

        // this.setState({ 
        //     [event.target.name]: event.target.value 
        // });
    }

    handleSubmit(event){
        console.log(this.name, this.email, this.country, this.married);

    }

    render() {

        return (
            <div>
            <form onSubmit={this.handleSubmit}>
            <div className="form-group">
            <label >Name</label>
            <input type="text" className="form-control" placeholder="Name" name="name" onChange={this.handleChange}/>
            </div>
            <div className="form-group">
            <label>Email</label>
            <input type="text" className="form-control" placeholder="Email" name="email" onChange={this.handleChange}/>
            </div>
            <div className="form-group">
            <label>Country</label>
            <input type="text" className="form-control" placeholder="Country" name="country" onChange={this.handleChange}/>
            </div>
            <div className="form-group">
            <label>Married</label>
            <select className="form-control" name="married" onChange={this.handleChange}>
            <option value="no">No</option>
            <option value="yes">Yes</option>
            </select>
            </div>
            <button type="submit" className="btn btn-primary">Submit</button>
            </form>
            </div>
            )
        }
    }
