import { Grid } from "gridjs-react";
import React, { Component } from "react";
import "jquery";
import "datatables.net-dt/css/jquery.dataTables.css";
import "datatables.net-bs4/css/dataTables.bootstrap4.css";

const $ = require("jquery");
$.DataTable = require("datatables.net");

export default class Table extends Component {
  componentDidMount() {
    this.$el = $(this.el);
    this.$el.DataTable({
      columns: [
        { title: "Name" },
        { title: "Position" },
        { title: "Office" },
        { title: "Extn." },
        { title: "Start date" },
        { title: "Salary" },
      ],
    });
  }

  clickUpdateButton(event) {
    this.props.onUpdateButton(event.target.value);
  }

  clickDeleteButton(event) {
    this.props.onDeleteButton(event.target.value);
  }

  render() {
    return (
      <div>
        <table className="display" width="100%" ref={(el) => (this.el = el)}>
          <tr>
            <td>1</td>
            <td>2</td>
            <td>3</td>
            <td>4</td>
            <td>5</td>
            <td>
              <button
                type="button"
                className="btn btn-primary"
                onClick={(event) => this.clickUpdateButton()}
              >
                Primary
              </button>
              <button
                type="button"
                className="btn btn-primary"
                onClick={(event) => this.clickDeleteButton()}
              >
                Primary
              </button>
            </td>
          </tr>
        </table>
      </div>
    );
  }
}
