export default function (keyword: string, tabular: Tabular): Tabular {
  // escape special regex chars
  keyword = keyword.replace(/[-[\]{}()*+?.,\\^$|#\s]/g, '\\$&');

  return new Tabular(
    tabular.rows.filter((row) =>
      row.cells.some((cell) => {
        if (!cell || !cell.data) {
          return false;
        }

        let data = '';

        if (typeof cell.data === 'object') {
          // HTMLContent element
          const element = cell.data as VNode<HTMLContentProps>;
          if (element.props && element.props.content) {
            // TODO: we should only search in the content of the element. props.content is the entire HTML element
            data = element.props.content;
          }
        } else {
          // primitive types
          data = String(cell.data);
        }

        return new RegExp(keyword, 'gi').test(data);
      }),
    ),
  );
}
export function className(...args: string[]): string {
  const prefix = 'gridjs';

  return `${prefix}${args.reduce(
    (prev: string, cur: string) => `${prev}-${cur}`,
    '',
  )}`;
}

export function classJoin(...classNames: string[]): string {
  return classNames
    .filter((x) => x)
    .reduce((className, prev) => `${className || ''} ${prev}`, '')
    .trim();
}
