                }}
              >
                {col.name}
              </th>
            );
          })}
        </tr>
      </thead>
    );
  }

  td(cell: Cell) {
    return <td style={this.resetStyle()}>{cell.data}</td>;
  }

  tr(row: Row) {
    return (
      <tr style={this.resetStyle()}>
        {row.cells.map((cell: Cell) => {
          return this.td(cell);
        })}
      </tr>
    );
  }

  body() {
    return (
      <tbody style={this.resetStyle()}>
        {this.props.data &&
          this.props.data.rows.map((row: Row) => {
            return this.tr(row);
          })}
      </tbody>
    );
  }

  render() {
    return (
      <table
        style={{
          position: 'absolute',
          zIndex: '-2147483640',
          visibility: 'hidden',
          tableLayout: 'auto',
          width: 'auto',
          ...this.resetStyle(),
        }}
