export enum PluginPosition {
  Header,
  Footer,
}

export interface Plugin {
  id: string;
  position: PluginPosition;
  component: VNode<any>;
  order?: number;
}

export class PluginManager {
  private readonly plugins: Plugin[];

  constructor() {
    this.plugins = [];
  }

  get(id: string): Plugin | null {
    const plugins = this.plugins.filter((p) => p.id === id);

    if (plugins.length > 0) {
      return plugins[0];
    }

    return null;
  }

  add(plugin: Plugin): this {
    if (!plugin.id) {
      log.error('Plugin ID cannot be empty');
      return this;
    }

    if (this.get(plugin.id) !== null) {
      log.error(`Duplicate plugin ID: ${plugin.id}`);
      return this;
    }

    this.plugins.push(plugin);
    return this;
  }

  remove(id: string): this {
    this.plugins.splice(this.plugins.indexOf(this.get(id)), 1);
    return this;
  }

  list(position?: PluginPosition): Plugin[] {
    let plugins: Plugin[];

    if (position != null || position != undefined) {
      plugins = this.plugins.filter((p) => p.position === position);
    } else {
      plugins = this.plugins;
    }

    return plugins.sort((a, b) => a.order - b.order);
  }
}

export interface PluginRendererProps extends BaseProps {
  position?: PluginPosition;
}

export class PluginRenderer extends BaseComponent<PluginRendererProps, {}> {
  render() {
    return h(
      Fragment,
      {},
      this.config.plugin.list(this.props.position).map((p) => p.component),
    );
  }
}
export function camelCase(str: string): string {
  return str.replace(/(?:^\w|[A-Z]|\b\w|\s+)/g, function (match, index) {
    if (+match === 0) return ''; // or if (/\s+/.test(match)) for white spaces
    return index === 0 ? match.toLowerCase() : match.toUpperCase();
  });
}
const _prefix = 'ID_';

/**
 * This class is mostly based on Flux's Dispatcher by Facebook
 * https://github.com/facebook/flux/blob/master/src/Dispatcher.js
 */
export default class Dispatcher<TPayload> {
  _callbacks: { [key: string]: (payload: TPayload) => void };
  _isDispatching: boolean;
  _isHandled: { [key: string]: boolean };
  _isPending: { [key: string]: boolean };
  _lastID: number;
  _pendingPayload: TPayload;

  constructor() {
    this._callbacks = {};
    this._isDispatching = false;
    this._isHandled = {};
    this._isPending = {};
    this._lastID = 1;
  }
  /**
   * Registers a callback to be invoked with every dispatched payload. Returns
   * a token that can be used with `waitFor()`.
   */
  register(callback: (payload: TPayload) => void): string {
    const id = _prefix + this._lastID++;
    this._callbacks[id] = callback;
    return id;
  }
  /**
   * Removes a callback based on its token.
   */
  unregister(id: string): void {
    if (!this._callbacks[id]) {
      throw Error(
        `Dispatcher.unregister(...): ${id} does not map to a registered callback.`,
      );
    }

    delete this._callbacks[id];
  }
  /**
   * Waits for the callbacks specified to be invoked before continuing execution
   * of the current callback. This method should only be used by a callback in
   * response to a dispatched payload.
   */
  waitFor(ids: Array<string>): void {
    if (!this._isDispatching) {
      throw Error(
        'Dispatcher.waitFor(...): Must be invoked while dispatching.',
      );
    }

    for (let ii = 0; ii < ids.length; ii++) {
      const id = ids[ii];
      if (this._isPending[id]) {
        if (!this._isHandled[id]) {
          throw Error(`Dispatcher.waitFor(...): Circular dependency detected while ' +
            'waiting for ${id}.`);
        }
        continue;
      }
      if (!this._callbacks[id]) {
        throw Error(
          `Dispatcher.waitFor(...): ${id} does not map to a registered callback.`,
        );
      }

      this._invokeCallback(id);
    }
  }
  /**
   * Dispatches a payload to all registered callbacks.
   */
  dispatch(payload: TPayload): void {
    if (this._isDispatching) {
      throw Error(
        'Dispatch.dispatch(...): Cannot dispatch in the middle of a dispatch.',
      );
    }

    this._startDispatching(payload);
    try {
      for (const id in this._callbacks) {
        if (this._isPending[id]) {
          continue;
        }
        this._invokeCallback(id);
      }
    } finally {
      this._stopDispatching();
    }
  }
  /**
   * Is this Dispatcher currently dispatching.
   */
  isDispatching(): boolean {
    return this._isDispatching;
  }
  /**
   * Call the callback stored with the given id. Also do some internal
   * bookkeeping.
   *
   * @internal
   */
  private _invokeCallback(id: string): void {
    this._isPending[id] = true;
    this._callbacks[id](this._pendingPayload);
    this._isHandled[id] = true;
  }
  /**
   * Set up bookkeeping needed when dispatching.
   *
   * @internal
   */
  private _startDispatching(payload: TPayload): void {
    for (const id in this._callbacks) {
      this._isPending[id] = false;
      this._isHandled[id] = false;
    }
    this._pendingPayload = payload;
    this._isDispatching = true;
  }
  /**
   * Clear bookkeeping used for dispatching.
   *
   * @internal
   */
  private _stopDispatching(): void {
    delete this._pendingPayload;
    this._isDispatching = false;
  }
}
/**
 * Base Storage class. All storage implementation must inherit this class
 */
import { TData } from '../types';

abstract class Storage<I> {
  /**
   * Returns all rows based on ...args
   * @param args
   */
  abstract async get(...args): Promise<StorageResponse>;

  /**
   * To set all rows
   *
   * @param data
   */
  set?(data: I | Function): this;
}

export interface StorageResponse {
  data: TData;
  total: number;
}

export default Storage;
import Storage, { StorageResponse } from './storage';
import { TData } from '../types';

class MemoryStorage extends Storage<TData> {
  private data: (() => TData) | (() => Promise<TData>);

  constructor(data: TData | (() => TData) | (() => Promise<TData>)) {
    super();
    this.set(data);
  }

  public async get(): Promise<StorageResponse> {
    const data = await this.data();

    return {
      data: data,
      total: data.length,
    };
  }

  public set(data: TData | (() => TData) | (() => Promise<TData>)): this {
    if (data instanceof Array) {
      this.data = (): TData => data;
    } else if (data instanceof Function) {
      this.data = data;
    }

    return this;
  }
}

