import React, { Component } from 'react'
import 'jquery/src/jquery'
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.min';


var mysql = require('mysql');
var pool = mysql.createPool({
    host: "localhost",
    user: "root",
    password: "root",
    database: "reactjs-crud-page"
  });

  pool.getConnection(function(err, connection) {
    if (err) throw err; // not connected!

    // Use the connection
    connection.query('SELECT something FROM sometable', function (error, results, fields) {
      // When done with the connection, release it.
      connection.release();

      // Handle error after the release.
      if (error) throw error;

      // Don't use the connection here, it has been returned to the pool.
    });
  });


const save = (event) => {
    event.preventDefault()




}




export default class Form extends Component {

    render() {
        return (
            <div>
                <h2 className="text-center" style={{color: "green"}}>Create</h2>
            <form onSubmit={save}>
            <div className="form-group">
            <label >Name</label>
            <input type="text" className="form-control" placeholder="Name" name="name"/>
            </div>
            <div className="form-group">
            <label>Country</label>
            <input type="text" className="form-control" placeholder="Country" name="country"/>
            </div>
            <div className="form-group">
            <label >Sex</label>
            <label className="radio-inline ml-2">
            <input type="radio" name="sex" defaultChecked/>Male
            </label>
            <label className="radio-inline ml-2">
            <input type="radio" name="sex"/>Female
            </label>
            <div className="form-group">
            <label>Married</label>
            <select className="form-control" name="married">
            <option value="no">No</option>
            <option value="yes">Yes</option>
            </select>
            </div>
            </div>
            <button type="submit" className="btn btn-primary">Submit</button>
            </form>
            </div>
            )
        }
    }
