import React, { Component } from 'react'
import 'jquery/src/jquery'
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.min';
import axios from 'axios'


function createSubmit(data){

}

function updateSubmit(){

}

function deleteSubmit(){

}

export default class Form extends Component {
    constructor(props){
        super(props);
        this.state = {
            name : '',
            email : '',
            country : '',
            married : 'no',

            status: "create",
            isSubmit:"create"
        };

        var headerText = "";
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);




    }

    handleChange = (event) => {
        this.setState({ 
            [event.target.name]: event.target.value 
        });


    }

    handleSubmit(event){


        switch(this.state.status){
            case "create":
            headerText = "Create";
            break;
            case "update":
            headerText = "Update";
            break;
            case "delete":
            headerText = "Delete";
            break;
        }

        event.preventDefault();


    }

    render() {



        switch(this.state.status){
            case "create":
            headerText = "Create";
            break;
            case "update":
            headerText = "Update";
            break;
            case "delete":
            headerText = "Delete";
            break;
        }

        return (
            <div>
            <h2 className="text-center">{headerText}</h2>
            <form onSubmit={this.handleSubmit}>
            <div className="form-group">
            <label >Name</label>
            <input type="text" className="form-control" placeholder="Name" name="name" onChange={this.handleChange}/>
            </div>
            <div className="form-group">
            <label>Email</label>
            <input type="text" className="form-control" placeholder="Email" name="email" onChange={this.handleChange}/>
            </div>
            <div className="form-group">
            <label>Country</label>
            <input type="text" className="form-control" placeholder="Country" name="country" onChange={this.handleChange}/>
            </div>
            <div className="form-group">
            <label>Married</label>
            <select className="form-control" name="married" value={this.state.married} onChange={this.handleChange}>
            <option value="no">No</option>
            <option value="yes">Yes</option>
            </select>
            </div>
            <button type="submit" className="btn btn-primary">Submit</button>
            </form>
            </div>
            )
        }
    }
