import React, { Component } from 'react'
import 'jquery/src/jquery'
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.min';
import axios from 'axios'



export default class Form extends Component {
    constructor(props){
        super(props);
        this.state = {
            name : '',
            email : '',
            country : '',
            married : 'no',

            isCreate: true,
            isCreateSubmit: false,

            isUpdate: false,
            isUpdateSubmit: false,

            isDelete: false,
            isDeleteSubmit: false,
        };

        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange = (event) => {
        this.setState({ 
            [event.target.name]: event.target.value 
        });


    }

    handleSubmit(event){
        this.setState({
            isSubmit: true
        });
        event.preventDefault();
        console.log("start submit outside");

        fetch("http://"+process.env.REACT_APP_BACKEND,{
        mode: "no-cors",
    })
    .then(response => console.log(response))

    this.forceUpdate();

    console.log("finish submit outside");


}

render() {

    var headerText;
    if(this.isCreate){
headerText = "Create";
    }

    return (
        <div>
            <h2 className="text-center">{headerText}</h2>
        <form onSubmit={this.handleSubmit}>
        <div className="form-group">
        <label >Name</label>
        <input type="text" className="form-control" placeholder="Name" name="name" onChange={this.handleChange}/>
        </div>
        <div className="form-group">
        <label>Email</label>
        <input type="text" className="form-control" placeholder="Email" name="email" onChange={this.handleChange}/>
        </div>
        <div className="form-group">
        <label>Country</label>
        <input type="text" className="form-control" placeholder="Country" name="country" onChange={this.handleChange}/>
        </div>
        <div className="form-group">
        <label>Married</label>
        <select className="form-control" name="married" value={this.state.married} onChange={this.handleChange}>
        <option value="no">No</option>
        <option value="yes">Yes</option>
        </select>
        </div>
        <button type="submit" className="btn btn-primary">Submit</button>
        </form>
        </div>
        )
    }
}
