import React, { Component } from 'react'
import 'jquery/src/jquery'
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.min';
import Toast from './Toast'


 async function PostData(data){

    const resp =  await fetch("http://localhost:3001",{
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
          },
        body: JSON.stringify(data),
    }
    );

return resp;
}

function updateSubmit(){

}

function deleteSubmit(){

}

export default class Form extends Component {
    constructor(props){
        super(props);
        this.state = {
            id: 0,
            name : '',
            email : '',
            country : '',
            married : 'no',

            status: "create",
            isSubmit:"create",

            toast: '',
        };

        var headerText = "";
        var data = {

        }



        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);




    }

    handleChange = (event) => {
        this.setState({
            [event.target.name]: event.target.value
        });


    }

    handleSubmit(event){

        switch(this.state.status){
            case "create":
            var data = {
                name : this.state.name,
                email : this.state.email,
                country : this.state.country,
                married : this.state.married,
            }

            const resp = PostData(data).then((v) => {
                return v.json();

            }).then((response)=>{
                if(response.affectedRows){
                    console.log(this.state.toast);

                    this.state.toast = <Toast msg="Data Successfully Added"/>;
                }else{
                    console.log(this.state.toast);
                    this.state.toast = <Toast msg="System Error"/>;
                }

            });




            break;
            case "update":
            this.headerText = "Update";
            break;
            case "delete":
            this.headerText = "Delete";
            break;
        }

        event.preventDefault();


    }

    render() {



        switch(this.state.status){
            case "create":
            this.headerText = "Create";
            break;
            case "update":
            this.headerText = "Update";
            break;
            case "delete":
            this.headerText = "Delete";
            break;
        }

        return (
            <div>
                {this.toast}
            <h2 className="text-center">{this.headerText}</h2>
            <form onSubmit={this.handleSubmit}>
            <div className="form-group">
            <label >Name</label>
            <input type="text" className="form-control" placeholder="Name" name="name" onChange={this.handleChange}/>
            </div>
            <div className="form-group">
            <label>Email</label>
            <input type="text" className="form-control" placeholder="Email" name="email" onChange={this.handleChange}/>
            </div>
            <div className="form-group">
            <label>Country</label>
            <input type="text" className="form-control" placeholder="Country" name="country" onChange={this.handleChange}/>
            </div>
            <div className="form-group">
            <label>Married</label>
            <select className="form-control" name="married" value={this.state.married} onChange={this.handleChange}>
            <option value="no">No</option>
            <option value="yes">Yes</option>
            </select>
            </div>
            <button type="submit" className="btn btn-primary">Submit</button>
            </form>
            </div>
            )
        }
    }
