import React, { Component } from 'react'
import 'jquery/src/jquery'
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.min';
import axios from 'axios'



export default class Form extends Component {
    constructor(props){
        super(props);
        this.state = {
            name : '',
            email : '',
            country : '',
            married : 'no',
        };

        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange = (event) => {
        this.setState({ 
            [event.target.name]: event.target.value 
        });


    }

    handleSubmit(event){
        event.preventDefault();
        console.log("start submit outside");

        // const submit = () =>{
        //     console.log("start submit");

        //     const axios = require('axios').default;
        //     axios.post(process.env.REACT_APP_BACKEND,{
        //         name: this.state.name,
        //         email: this.state.email,
        //         country: this.state.country,
        //         married: this.state.married
        //         })
        //         .then(function(response){
        //             console.log(response);

        //         });
        //         console.log("finish submit");
        // };



        // // ==============================
        // const Options = {
        //     method: 'POST',
        //     headers: { 'Content-Type': 'application/json' },
        //     body: JSON.stringify({ title: 'React POST Request Example' })
        // };

        // const submit = await fetch(process.env.REACT_APP_BACKEND,Options).then(
        //     (response) => {
        //         console.log(response);
        //         console.log("done");

        //     }
        // )
        // // ============================


        axios.post(process.env.REACT_APP_BACKEND,{
                    name: this.state.name,
                    email: this.state.email,
                    country: this.state.country,
                    married: this.state.married
                    })
                    .then(function(response){
                        console.log(response);

                    });


        console.log("finish submit outside");


    }

    render() {

        return (
            <div>
            <form onSubmit={this.handleSubmit}>
            <div className="form-group">
            <label >Name</label>
            <input type="text" className="form-control" placeholder="Name" name="name" onChange={this.handleChange}/>
            </div>
            <div className="form-group">
            <label>Email</label>
            <input type="text" className="form-control" placeholder="Email" name="email" onChange={this.handleChange}/>
            </div>
            <div className="form-group">
            <label>Country</label>
            <input type="text" className="form-control" placeholder="Country" name="country" onChange={this.handleChange}/>
            </div>
            <div className="form-group">
            <label>Married</label>
            <select className="form-control" name="married" value={this.state.married} onChange={this.handleChange}>
            <option value="no">No</option>
            <option value="yes">Yes</option>
            </select>
            </div>
            <button type="submit" className="btn btn-primary">Submit</button>
            </form>
            </div>
            )
        }
    }
