import { Grid } from "gridjs-react";
import React, { Component } from "react";
import "jquery";
import "datatables.net-dt/css/jquery.dataTables.css";
import "datatables.net-bs4/css/dataTables.bootstrap4.css";
import {HttpGet, HttpPost} from '../utils/Req'

const $ = require("jquery");
$.DataTable = require("datatables.net");

export default class Table extends Component {


  constructor(props){
    super(props);
    this.state = {
      data : [],
    }
  }

  componentDidMount() {
    // this.$el = $(this.el);
    // this.$el.DataTable({
    //   columns: [
    //     { title: "Name" },
    //     { title: "Email" },
    //     { title: "Country" },
    //     { title: "Married" },
    //     { title: "" },
    //     { title: "" },
    //   ],
    // });


    const response = HttpGet('http://localhost:3001').then((value)=>{
      return value.json();
    }).then((value)=>{
      this.setState({
        data: value
      })
    })
  }

  clickUpdateButton(value) {
    console.log(value);
    this.props.onUpdateButton(value);
  }

  clickDeleteButton = () => {
    console.log("deletebutton");
    //this.props.onDeleteButton(value);
  }

  render() {
    return (
      <div>
        <table className="display" width="100%" ref={(el) => (this.el = el)}>

        <thead>
          <th>1</th>
          <th>1</th>
          <th>1</th>
          <th>1</th>
          <th>1</th>
        </thead>
        <tbody>
        {this.state.data.map(value=>(
          <tr>
            <td>{value.name}</td>
            <td>{value.email}</td>
            <td>{value.coutnry}</td>
            <td>{value.married}</td>
            <td>              
            <button
                type="button"
                className="btn btn-primary"
                onClick={() => this.clickUpdateButton(value.id)}
              >
                Update
              </button>
                </td>
            <td>            
              <button
                type="button"
                className="btn btn-primary"
                onClick={() => this.clickUpdateButton(value.id)}
              >
                Delete
              </button></td>
          </tr>
        ))}
        </tbody>
        </table>
      </div>
    );
  }
}
