import React, { Component } from 'react'
import 'jquery/src/jquery'
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.min';
import Toast from './Toast'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import axios from 'axios';

async function HttpGet(url){

    const resp =  await fetch(url,{
        method: 'GET',
    }
    );
    return resp;
}

async function HttpPost(data){

    const resp =  await fetch("http://localhost:3001",{
    method: 'GET',
    headers: {
        'Content-Type': 'application/json'
    }
}
);
return resp;
}

async function getExistingData(id){
    try{
        const response = await axios.get('http://localhost:8081/'+id)
        console.log(response);
    }catch(error){
        console.log(error);
    }
}

export default class Form extends Component {
    constructor(props){
        super(props);
        this.state = {
            id: 0,
            name : '',
            email : '',
            country : '',
            married : 'no',

            headerName: this.props.headerName
        };






        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);    

    }




    handleChange = (event) => {
        this.setState({
            [event.target.name]: event.target.value
        });


    }

    handleSubmit(event){

        switch(this.state.status){
            case "create":
            var data = {
                name : this.state.name,
                email : this.state.email,
                country : this.state.country,
                married : this.state.married,
            }

            // const resp = PostData(data).then((v) => {
            //     return v.json();

            // }).then((response)=>{
            //     if(response.affectedRows){
            //         console.log(2);

            //         const notify = () => toast("2");
            //     }else{
            //         console.log(1);
            //         const notify = () => toast("1");
            //     }

            // });




            break;
            case "update":
            this.headerText = "Update";
            break;
            case "delete":
            this.headerText = "Delete";
            break;
        }

        event.preventDefault();


    }

    existingData(id){
        const response =  HttpGet('http://localhost:3001/1').then((response)=>{
        // var result = response.text();
        return response.json();

    }).then((person) => {
        console.log(person.name);
        this.setState({
            name: person.name,
            email: person.email,
            country: person.country,
            married: person.married
        });
    }).catch((rejected) => {
        console.log(rejected);
    });



}

render() {
    var marriedYes, marriedNo;
    if(this.state.married == "yes"){
        marriedYes = "selected";
        marriedNo = "";
    }else{
        marriedYes = "";
        marriedNo = "selected";
    }
    return (
        <div>
        <ToastContainer />
        <h2 className="text-center">{this.props.headerName}</h2>
        <form onSubmit={this.handleSubmit}>
        <input type="hidden" name="id" value={this.state.id}/>
        <div className="form-group">
        <label >Name</label>
        <input type="text" className="form-control" placeholder="Name" name="name" onChange={this.handleChange} value={this.state.name}/>
        </div>
        <div className="form-group">
        <label>Email</label>
        <input type="text" className="form-control" placeholder="Email" name="email" onChange={this.handleChange} value={this.state.email}/>
        </div>
        <div className="form-group">
        <label>Country</label>
        <input type="text" className="form-control" placeholder="Country" name="country" onChange={this.handleChange} value={this.state.country}/>
        </div>
        <div className="form-group">
        <label>Married</label>
        <select className="form-control" name="married" value={this.state.married} onChange={this.handleChange}>
        <option value="no" selected={marriedYes}>No</option>
        <option value="yes" selected={marriedNo}>Yes</option>
        </select>
        </div>
        <button type="submit" className="btn btn-primary">Submit</button>
        </form>
        </div>
        )
    }
}
