import React from "react";
import logo from "./logo.svg";
import "./App.css";
import Header from "./components/Header";
import Form from "./components/Form";
import Table from "./components/Table";

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = ({
      headerName: "Create",
    });
  }

  handleUpdateButton(value) {
    this.setState({
      headerName: value,
    });
  }

  handleDeleteButton(value) {
    this.setState({
      headerName: value,
    });
  }

  render() {
    return (
      <div className="container-fluid">
        <Header  />
        <div className="row">
          <div className="col-md-6">
            <Form headerName={this.state.headerName}/>
          </div>
          <div className="col-md-6">
            <Table
              onUpdateButton={(value) => this.handleUpdateButton()}
              onDeleteButton={(value) => this.handleDeleteButton()}
            />
          </div>
        </div>
      </div>
    );
  }
}
